import React, { useState, useEffect } from "react";
import Folder from "./folder";
import File from "./file";
import "./App.css";

export default function Explorer({ folderItems }) {
  let nestedFolder = [];
  const [items, setItems] = useState(folderItems);
  const [inputValue, setInputValue] = useState("");

  function toggleFolder() {
    items.isOpen = !items.isOpen;
    setItems({ ...items });
  }

  function addNewFolder() {
    items.children.push({
      type: "folder",
      name: "",
      isOpen: false,
      children: [],
    });
    items.isOpen = true;
    setItems({ ...items });
  }

  function addNewFile() {
    items.children.push({
      type: "file",
      name: "",
      children: [],
    });
    items.isOpen = true;
    setItems({ ...items });
  }

  function emptyFolder() {
    items.children = [];
    setItems({ ...items });
  }

  if (items.children.length > 0) {
    nestedFolder = items.children.map((item, index) => (
      <Explorer key={index} folderItems={item} />
    ));
  }

  return (
    <div style={{ marginLeft: "25px", marginTop: "10px" }}>
      {" "}
      //use classname
      <div className="displayHierarchy">
        {" "}
        // name change
        {items.type === "folder" ? (
          <>
            <Folder
              folderItems={items} // remove from prop name
              folderName={
                items.name ? (
                  items.name
                ) : (
                  <input
                    type="text"
                    placeholder="enter the folder name here"
                    value={inputValue}
                    onChange={(event) => {
                      setInputValue(event.target.value);
                    }}
                    onBlur={(event) => {
                      items.name = event.target.value;
                      items.isOpen = true;
                      setItems({ ...items });
                    }}
                  />
                )
              }
              open={items.isOpen && items.children.length ? "open" : "close"}
              toggleFolder={toggleFolder} // change callback function name
            />
            <button onClick={addNewFolder}>New Folder</button>
            <button onClick={addNewFile}>New File</button>
            <button onClick={emptyFolder}>Empty Folder</button> // styling
          </>
        ) : (
          <File
            fileName={
              // remove file from prop name
              items.name ? (
                items.name
              ) : (
                <input
                  type="text"
                  placeholder="enter the file name here"
                  value={inputValue}
                  onChange={(event) => {
                    setInputValue(event.target.value);
                  }}
                  onBlur={(event) => {
                    items.name = event.target.value;
                    setItems({ ...items });
                  }}
                />
              )
            } // create a function and do both folder and file functionality there
          />
        )}
      </div>
      {items.isOpen ? nestedFolder : null}
    </div>
  );
}
