import React, { useState } from 'react'
import './App.css'
import Mapper from './mapper.js'

function App() {

  const [folders,setFolders] = useState(0)

  function addNewFolder(){
      setFolders(folders+1)
  }

  function deleteFolder(){
    if(folders>0){
      setFolders(folders-1)
    }
  }

  return (
    <>
      <div>
        <button onClick={addNewFolder}>New Folder</button>
        <button onClick ={deleteFolder}>Delete Folder</button>
      </div>
      <Mapper />
    </>
  );
}

export default App;
