import React , {useEffect, useState} from 'react'
import Folder from './folder'
import File from './file'
import './App.css'

function Mapper({folderItems}){
  let nestedFolder =[]
  const [items , setItems] = useState(folderItems)

  function handleImgDivClick(){
    console.log(items)
    const a = {
      ...items, 
      isOpen: !items.isOpen,
    }
    console.log("inside div click", a)
    setItems(a)
  }

  function addToFolder(newObj){
    console.log(items.insideFolder)
    const a = {
      ...items, 
      isOpen: true,
      insideFolder: [...items.insideFolder, newObj]
    }
    console.log("inside addToFolder", a)
    setItems(a)
  }

  function addNewFolder(items){
    console.log({items})
    const newObj = 
      {
        type : 'Folder',
        name : '',
        isOpen : false,
        insideFolder : []
    }
    addToFolder(newObj)
  }

  function addNewFile(items){
    console.log({items})
    const newObj = 
      {
        type : 'File',
        name : '',
        insideFolder : []
    }
    addToFolder(newObj)
  }

  if(items.insideFolder && items.insideFolder.length > 0 ){
    nestedFolder = items.insideFolder.map((item,index)=>{
      return (
        <Mapper key ={index} folderItems = {item} />
      )
    })
  } 

  return (
    <div style = {{"marginLeft" : "25px" , "marginTop" : "10px" }}>
      <div className ='displayHierarchy'>
        {items.type === "Folder" ? 
            <>
              <Folder 
                folderItems={items}
                open ={items.isOpen ? 'open' : 'close'}
                handleImgDivClick={handleImgDivClick}
              />
              <button onClick={()=>addNewFolder(items)}>New Folder</button>
              <button onClick={()=>addNewFile(items)}>New File</button>
            </>
          : 
          <File fileName ={items.name}/>
        }
      </div>
        {items.isOpen ? nestedFolder : null}
    </div>
  )

}
export default Mapper;
