import React , {useState} from 'react'
import Folder from './folder'
import File from './file'
import './App.css'

function Mapper({folderItems}){
  let nestedFolder =[]
  const [open , setOpen] = useState(folderItems.isOpen ? 'open' : 'close')
  const [isOpen ,setIsOpen] = useState(folderItems.isOpen)

  function handleImgDivClick(){
    setIsOpen(!isOpen)
    setOpen(isOpen ? 'open' : 'close')
  }

  function addNewFolder(folderData){
    folderData.push(
      {
        type : 'Folder',
        name : '',
        isOpen : false,
        insideFolder : []
    })
    setIsOpen(true)
  }

  function addNewFile(folderData){
    folderData.push(
      {
        type : 'File',
        name : '',
        insideFolder : []
    })
    setOpen('open')
    setLength(length + 1)
  }

  if(folderItems.insideFolder.length > 0 ){
    nestedFolder = folderItems.insideFolder.map((item,index)=>{
      return (
        <Mapper key ={index} folderItems = {item} />
      )
    })
  } 

  return (
    <div style = {{"marginLeft" : "25px" , "marginTop" : "10px" }}>
      <div className ='displayHierarchy'>
        {folderItems.type === "Folder" ? 
            <>
              <Folder 
                folderItems={folderItems}
                open ={open}
                handleImgDivClick={handleImgDivClick}
              />
              <button onClick={()=>addNewFolder(folderItems.insideFolder)}>New Folder</button>
              <button onClick={()=>addNewFile(folderItems.insideFolder)}>New File</button>
            </>
          : 
          <File fileName ={folderItems.name}/>
        }
      </div>
        {isOpen ? nestedFolder : null}
    </div>
  )

}
export default Mapper;
