import React, { useState } from 'react'
import './App.css'

function Folder() {
  const [open , setOpen] = useState('close')  

  function handleImgDivClick(){
    console.log(open)
    if(open==='open'){
      setOpen('close')
    } else {
      setOpen('open')
    }
  }


  return (
      <>
        <img 
            src={`./images/${open}.png`} 
            alt='folder' 
            onClick={handleImgDivClick} 
        />
        <button>Add New Folder</button>
        <button>Add New file</button>

      </>
  );
}

export default Folder;
