import React, { useState } from 'react'
import './App.css'

function Folder({open,handleImgDivClick,folderItems}) {
  function addNewFolder(folderData){
    folderData.push(
      {
        type : 'Folder',
        name : '',
        isOpen : false,
        insideFolder : []
    })
    setOpen('open')
    setLength(folderData.length)
  }

  function addNewFile(folderData){
    folderData.push(
      {
        type : 'File',
        name : '',
        insideFolder : []
    })
    setOpen('open')
    setLength(folderData.length)
  }

  return (
      <>
        <img 
            src={`./images/${open}.png`} 
            alt='folder' 
            onClick={handleImgDivClick} 
        />
        {folderItems.name}
        <button onClick={()=>addNewFolder(folderItems.insideFolder)}>New Folder</button>
        <button onClick={()=>addNewFile(folderItems.insideFolder)}>New File</button>
      </>
  );
}

export default Folder;
