import React, { useState } from 'react'
import './App.css'

function Folder({isOpen}) {
//   const [open , setOpen] = useState('open')  
  const open = isOpen ? 'open' : 'close'
  function handleImgDivClick(){
    if(open==='open'){
      setOpen('close')
    } else {
      setOpen('open')
    }
  }


  return (
      <>
        <img 
            src={`./images/${open}.png`} 
            alt='folder' 
            onClick={handleImgDivClick} 
        />
      </>
  );
}

export default Folder;
