import React , {useState} from 'react'
import Folder from './folder'
import File from './file'
import './App.css'

function Mapper({folderItems}){
  let nestedFolder =[]
  const [open , setOpen] = useState(folderItems.isOpen)
  const [length , setLength ] = useState(folderItems.insideFolder.length)
  const [items , setItems] = useState(folderItems)

  function handleImgDivClick(){
    items.isOpen = !items.isOpen
    setOpen(items.isOpen)
  }

  function addToFolder(){
    items.isOpen = true
    setOpen(items.isOpen)
    setLength(length+1)
  }

  function addNewFolder(folderData){
    folderData.push(
      {
        type : 'Folder',
        name : '',
        isOpen : false,
        insideFolder : []
    })
    addToFolder()
  }

  function addNewFile(folderData){
    folderData.push(
      {
        type : 'File',
        name : '',
        insideFolder : []
    })
    addToFolder()
  }

  if(items.insideFolder.length > 0 ){
    nestedFolder = items.insideFolder.map((item,index)=>{
      return (
        <Mapper key ={index} folderItems = {item} />
      )
    })
  } 

  return (
    <div style = {{"marginLeft" : "25px" , "marginTop" : "10px" }}>
      <div className ='displayHierarchy'>
        {items.type === "Folder" ? 
            <>
              <Folder 
                folderItems={items}
                open ={open ? 'open' : 'close'}
                handleImgDivClick={handleImgDivClick}
              />
              <button onClick={()=>addNewFolder(items.insideFolder)}>New Folder</button>
              <button onClick={()=>addNewFile(items.insideFolder)}>New File</button>
            </>
          : 
          <File fileName ={items.name}/>
        }
      </div>
        {open && length ? nestedFolder : null}
    </div>
  )

}
export default Mapper;
