import React, { useState ,useEffect } from 'react'
import {folderStructure} from './folder-structure'
import './App.css'

function Folder({folderCount,...restProps}) {
  const [open , setOpen] = useState('close')  
  const [folderDiv] = useState([])



  console.log(folderCount)

  function handleImgDivClick(){
    console.log(open)
    if(open==='open'){
      setOpen('close')
    } else {
      setOpen('open')
    }
  }


  return (
      <>
        {folderStructure.map(item=>{
            if(item.type==='Folder'){
               return  <img src={`./images/${open}.png`} alt='folder' onClick={handleImgDivClick}/>
            }
        })}
      </>
  );
}

export default Folder;
