import React, { useEffect } from 'react'
import Folder from './folder'
import File from './file'
import {folderStructure} from './folder-structure'
import './App.css'

function Mapper () {
  function Map({arrItem}) {
      const nestedFolder = arrItem.insideFolder.map((item,index)=>{
        return <Map key = {index} arrItem ={item} />
      })

      return (
        <div style={{"marginLeft": "25px", "marginTop": "10px"}}>
          <div>{arrItem.name}</div>
          {nestedFolder}
        </div>
      )
    }


  return (
      <>
        {folderStructure.map((item,index)=>{
          return <Map key = {index} arrItem ={item} />
        })}
      </>
  );
}

export default Mapper;
