import React, { useEffect } from 'react'
import Folder from './folder'
import File from './file'
import {folderStructure} from './folder-structure'
import './App.css'

function Mapper() {
  function mapRecursive(folderStructure){
    folderStructure.map((item,index)=>{
        if(item.type==='Folder' && item.insideFolder.length===0){
          return <Folder key = {index}/>
        } else if (item.type==='Folder' && item.insideFolder.length>0){
          return (
          <>
            <Folder key = {index}/>
            {mapRecursive(item.insideFolder)}
          </>
          )

        }
        else if (item.type==='File'){
          return <File key = {index}/>
        }
        return null
    })
  }


  return (
      <>
        {mapRecursive(folderStructure)}
      </>
  );
}

export default Mapper;
