import React, { useState ,useEffect } from 'react'
import './App.css'

function Root() {
  const [open , setOpen] = useState('close')  

  function handleImgDivClick(){
    console.log(open)
    if(open==='open'){
      setOpen('close')
    } else {
      setOpen('open')
    }
  }

  return (
    <>
      <img src={`./images/${open}.png`} alt='folder' onClick={handleImgDivClick}/>
    </>
  );
}

export default Root;
