import React, { useState ,useEffect } from 'react'
import './App.css'

let finalDisplay = ''

function Folder({folderCount,...restProps}) {
  const [open , setOpen] = useState('close')  

  console.log(folderCount)

  function handleImgDivClick(){
    console.log(open)
    if(open==='open'){
      setOpen('close')
    } else {
      setOpen('open')
    }
  }

  function folderMap(){
    let toDisplay =''
    for (let i = 0;i<folderCount;i++){
        toDisplay += `<div><img src='./images/${open}.png' alt='folder' onClick={handleImgDivClick}/><div>`
    }
    finalDisplay = toDisplay;
  }

  useEffect(()=>{
    folderMap();
  },[folderCount])

  return (
      <>
        {finalDisplay}
      </>
  );
}

export default Folder;
