import React, { useState, useEffect } from "react";
import Folder from "./folder";
import File from "./file";
import "./App.css";

function Mapper({ folderItems }) {
  let nestedFolder = [];
  const [items, setItems] = useState(folderItems);
  const [value, setValue] = useState("");
  const [showInput, setShowInput] = useState(items.name ? false : true);

  function handleImgDivClick() {
    items.isOpen = !items.isOpen;
    setItems({ ...items });
  }

  function addNewFolder() {
    items.insideFolder.push({
      type: "Folder",
      name: value,
      isOpen: false,
      insideFolder: [],
    });
    items.isOpen = true;
    setShowInput(false);
    // items.insideFolder[0].name = value;
    setItems({ ...items });
  }

  function addNewFile() {
    items.insideFolder.push({
      type: "File",
      name: "",
      insideFolder: [],
    });
    items.isOpen = true;
    setItems({ ...items });
  }

  if (items.insideFolder.length > 0) {
    nestedFolder = items.insideFolder.map((item, index) => (
      <Mapper key={index} folderItems={item} />
    ));
  }

  return (
    <div style={{ marginLeft: "25px", marginTop: "10px" }}>
      <div className="displayHierarchy">
        {items.type === "Folder" ? (
          <>
            <Folder
              folderItems={items}
              folderName={
                !showInput ? (
                  value || items.name
                ) : (
                  <input
                    type="text"
                    placeholder="enter the folder name here"
                    value={value}
                    onChange={(event) => setValue(event.target.value)}
                    // onBlur={(event) => setShowInput(false)}
                  />
                )
              }
              open={
                items.isOpen && items.insideFolder.length ? "open" : "close"
              }
              handleImgDivClick={handleImgDivClick}
            />
            <button onClick={addNewFolder}>New Folder</button>
            <button onClick={addNewFile}>New File</button>
          </>
        ) : (
          <File fileName={items.name} />
        )}
      </div>
      {items.isOpen ? nestedFolder : null}
    </div>
  );
}
export default Mapper;
