import React, { useState } from 'react'
import './App.css'

function Folder({folderItems}) {

  const [open , setOpen] = useState(folderItems.isOpen ? 'open' : 'close')

  function handleImgDivClick(){
    folderItems.isOpen = !folderItems.isOpen
    setOpen(folderItems.isOpen ? 'open' : 'close')
  }

  function addNewFolder(folderData){
    folderData.push(
      {
        type : 'Folder',
        name : '',
        isOpen : false,
        insideFolder : []
    })
  }

  function addNewFile(folderData){
    folderData.push(
      {
        type : 'File',
        name : '',
        insideFolder : []
    })
  }

  return (
      <>
        <img 
            src={`./images/${open}.png`} 
            alt='folder' 
            onClick={handleImgDivClick} 
        />
        {folderItems.name}
        <button onClick={()=>addNewFolder(folderItems.insideFolder)}>New Folder</button>
        <button onClick={()=>addNewFile(folderItems.insideFolder)}>New File</button>
      </>
  );
}

export default Folder;
