import React, { useEffect } from 'react'
import Folder from './folder'
import File from './file'
import {folderStructure} from './folder-structure'
import './App.css'

function Mapper () {
  function Map({arrItem,insideFolder}) {
    const nestedFolder =[];

      if(arrItem.insideFolder.length > 0) {
        nestedFolder = insideFolder.map((item,index)=>{
          return <Map key ={index} arrItem={item} insideFolder={item.insideFolder}/>
        })
      }

      return (
        <div style={{"marginLeft": "25px", "marginTop": "10px"}}>
          <div>{arrItem.Name}</div>
          {nestedFolder}
        </div>
      )
    }


  return (
      <>
        {folderStructure.insideFolder.map((item,index)=>{
          return <Map key = {index} arrItem ={item} insideFolder = {item.insideFolder} />
        })}
      </>
  );
}

export default Mapper;
