import React, { useState , useEffect } from 'react'
import './App.css'

function App() {
  const folderStatus = 'open';
  const [open , setOpen] = useState(false)

  useEffect(()=>{
    if(!open){
      folderStatus = 'open'
    } else {
      folderStatus ='close'
    }
  },[open])

  return (
    <div>
      <img src={`./images/${folderStatus}.png`} alt='folder' onClick={()=>setOpen(!open)}/>
    </div>
  );
}

export default App;
