import React , {useEffect, useState} from 'react'
import Folder from './folder'
import File from './file'
import './App.css'

function Mapper({folderItems, randomKey}){
  let nestedFolder =[]
  const [items , setItems] = useState(folderItems)
  const [isOpen,setIsOpen] = useState(items.isOpen)

  function handleImgDivClick() {
    setIsOpen(!isOpen)
    setItems({...items,isOpen : isOpen})
  }

  function addToFolder(newObj){
    const a = {
      ...items, 
      isOpen: true,
      insideFolder: [...items.insideFolder, newObj]
    }
    console.log("inside addToFolder", a)
    setItems(a)
  }

  function addNewFolder(randomKey,items){
    items.insideFolder.push(
      {
        type : 'Folder',
        name : `ABC${randomKey}`,
        isOpen : false,
        insideFolder : []
    })
    // const a = {
    //   ...items, 
    //   isOpen: true,
    //   insideFolder: [...items.insideFolder, newObj]
    // }
    setItems({...items})
    setIsOpen(true)
  }

  function addNewFile(){
    const newObj = 
      {
        type : 'File',
        name : '',
        insideFolder : []
    }
    addToFolder(newObj)
  }

  if(items.insideFolder.length > 0 ){
    nestedFolder = items.insideFolder.map((item,index)=>{
      return (
        <Mapper key ={index} folderItems = {item} randomKey={index}/>
      )
    })
  } 

  console.log(items)

  return (
    <div style = {{"marginLeft" : "25px" , "marginTop" : "10px" }}>
      <div className ='displayHierarchy'>
        {items.type === "Folder" ? 
            <>
              <Folder 
                folderItems={items}
                open ={isOpen ? 'open' : 'close'}
                handleImgDivClick={() => handleImgDivClick(items)}
              />
              <button onClick={()=>addNewFolder(randomKey,items)}>New Folder</button>
              <button onClick={()=>addNewFile()}>New File</button>
            </>
          : 
          <File fileName ={items.name}/>
        }
      </div>
        {isOpen ? nestedFolder : null}
    </div>
  )

}
export default Mapper;
